problem_type=#{item.problem_type}, algorithm=#{item.algorithm}, data_structure=#{item.data_structure}, difficulty=#{item.difficulty}, title=#{item.title}, description=#{item.description}, test_sum=#{item.test_sum}, add_time=#{item.add_time}, time_limit=#{item.time_limit}, memory_limit=#{item.memory_limit}
where problem_id=#{item.problem_id}
